import React from "react"
import styled from "@emotion/styled";
import { useField, Field, ErrorMessage } from "formik"

const StyledSelect = styled.select`
  color: var(--blue);
`;

const StyledErrorMessage = styled.div`
  font-size: 12px;
  color: var(--red-600);
  width: 400px;
  margin-top: 0.25rem;
  &:before {
    content: "❌ ";
    font-size: 10px;
  }
  @media (prefers-color-scheme: dark) {
    color: var(--red-300);
  }
`;

const StyledLabel = styled.label`
  margin-top: 0.2rem;
`;

function setStyle(error) {
  if (error) {
    return { border: '1px solid red' }
  }
}

const SelectComponent = ({ label, options, ...props }) => {
  const [field, meta] = useField(props);
  return (
    <>
      <StyledLabel htmlFor={props.id || props.name}>{label}</StyledLabel>
      <StyledSelect style={meta.error && meta.touched? setStyle(meta.error, props.name): null} {...field} {...props} >
      {options.map((x, y) => <option value={x.value} key={y}>{x.name}</option>)}</StyledSelect>
      {meta.touched && meta.error ? (
        <StyledErrorMessage>{meta.error}</StyledErrorMessage>
      ) : null}
    </>
  );
};

export default SelectComponent
